mapper.insert:
		INSERT 
		  INTO MEMBER
		VALUES ( 
				 #{ id }
		       , #{ pwd }
		       , #{ name }
		       , #{ email }
		       , #{ gender }
		       , #{ age }
		       , #{ phone }
		       , #{ address }
		       , sysdate
		       , sysdate
		       , default		
      	       )

mapper.update:
UPDATE 
       MEMBER
   SET 
       EMAIL=#{ email }
     , PWD=#{ pwd }
     , AGE =#{ age }
     , PHONE = #{ phone }
     , ADDRESS = #{ address }
     , UPDATE_DATE = SYSDATE
 WHERE ID = #{ id }
